#!/bin/bash

.  /local/env/envfastsimbac-1.0.1_bd3ad13d8f79.sh 

while read line ;  do   t=`echo $line |cut -d" " -f1`;  r=`echo $line |cut -d" " -f2`; delta=`echo $line |cut -d" " -f3`; TS=`echo $line |cut -d" " -f4`;    N2=`echo $line |cut -d" " -f5`;M12=`echo $line | cut -d" " -f7`; M21=`echo $line | cut -d" " -f8`; Tam=`echo $line | cut -d" " -f9`;  echo   ./fastSimBac 30 1712736  -T -t $t -r $r $delta -I 2 13 17 0 -m 1 2 0 -m 2 1 0 -n  1 1  -n 2 $N2 -ema $Tam 2 0  $M12 $M21 0  -ej $TS 1 2  > $SLURM_ARRAY_TASK_ID".line"; fastSimBac 30 1712736 -T -t $t -r $r $delta -I 2 13 17 0 -m 1 2 0 -m 2 1 0 -n  1 1  -n 2 $N2 -ema $Tam 2 0  $M12 $M21 0  -ej $TS 1 2  | msformatter > $SLURM_ARRAY_TASK_ID".temp2" ; if test -s $SLURM_ARRAY_TASK_ID.temp2; then echo  $SLURM_ARRAY_TASK_ID".temp2" >>log; cat   $SLURM_ARRAY_TASK_ID".line" $SLURM_ARRAY_TASK_ID".temp2"  >> "priorsAM-"$SLURM_ARRAY_TASK_ID.ms; else cat $SLURM_ARRAY_TASK_ID".line" segsite >>  "priorsAM-"$SLURM_ARRAY_TASK_ID.ms ; fi;done< priorsAM-$SLURM_ARRAY_TASK_ID 